INSERT INTO `category` VALUES (11,'Healthcare','Care sheets in this category relate to personal care delivered directly to the senior person by the carer.',1,'2023-9-21_13-18-0_573.png','#B19DE5'),(12,'Equipment','Care sheets and videos in this category relate to the use of equipment to support the care of a senior person.',1,'2023-9-21_13-18-0_532.png','#F88B6F'),(13,'Environment','Care sheets and videos in this category relate to the environment where the senior person lives that may impact on their health and safety. ',1,'2023-9-21_13-18-0_102.png','#4FD490'),(14,'Social needs','Care sheets and videos in this category relate to the social spiritual and mental health needs of a senior person.',1,'2023-9-21_13-19-0_560.png','#F9C26F'),(16,'Miscellaneous','Care items that do not fit into any other category ',1,'2023-9-21_13-18-0_240.png','#8B8279');
